import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Home, 
  Search, 
  Bookmark, 
  MessageCircle, 
  Share2, 
  Settings, 
  HelpCircle,
  Play,
  SkipForward,
  Plus,
  Trash2,
  Download,
  CheckCircle,
  XCircle,
  Clock,
  AlertCircle,
  Upload,
  Image
} from 'lucide-react';
import './App.css';

function App() {
  // States
  const [apiKeys, setApiKeys] = useState([]);
  const [newApiKey, setNewApiKey] = useState('');
  const [tasks, setTasks] = useState([]);
  const [prompts, setPrompts] = useState(['']);
  const [batchName, setBatchName] = useState('Batch 1');
  const [status, setStatus] = useState({});
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [selectedImage, setSelectedImage] = useState(null);
  const [uploadedImagePath, setUploadedImagePath] = useState('');

  // Load data on mount
  useEffect(() => {
    loadApiKeys();
    loadTasks();
    loadStatus();
    
    // Auto refresh every 5 seconds
    const interval = setInterval(() => {
      loadTasks();
      loadStatus();
    }, 5000);
    
    return () => clearInterval(interval);
  }, []);

  // API functions
  const loadApiKeys = async () => {
    try {
      const response = await axios.get('/api/keys');
      setApiKeys(response.data.keys);
    } catch (error) {
      console.error('Error loading API keys:', error);
    }
  };

  const loadTasks = async () => {
    try {
      const response = await axios.get('/api/tasks');
      setTasks(response.data.tasks);
    } catch (error) {
      console.error('Error loading tasks:', error);
    }
  };

  const loadStatus = async () => {
    try {
      const response = await axios.get('/api/status');
      setStatus(response.data);
    } catch (error) {
      console.error('Error loading status:', error);
    }
  };

  const addApiKey = async () => {
    if (!newApiKey.trim()) return;
    
    try {
      await axios.post('/api/keys', { key: newApiKey });
      setNewApiKey('');
      loadApiKeys();
      loadStatus();
    } catch (error) {
      alert('Error adding API key: ' + error.response?.data?.error || error.message);
    }
  };

  const removeApiKey = async (keySuffix) => {
    if (window.confirm('Bạn có chắc muốn xóa API key này?')) {
      try {
        await axios.delete(`/api/keys/${keySuffix}`);
        loadApiKeys();
        loadStatus();
      } catch (error) {
        alert('Error removing API key: ' + error.response?.data?.error || error.message);
      }
    }
  };

  const createBatchTask = async () => {
    const validPrompts = prompts.filter(p => p.trim());
    if (validPrompts.length === 0) {
      alert('Vui lòng nhập ít nhất một prompt');
      return;
    }

    if (apiKeys.length === 0) {
      alert('Vui lòng thêm ít nhất một API key');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post('/api/tasks', {
        prompts: validPrompts,
        name: batchName,
        input_image_path: uploadedImagePath || null
      });
      
      setPrompts(['']);
      setBatchName(`Batch ${tasks.length + 2}`);
      setSelectedImage(null);
      setUploadedImagePath('');
      loadTasks();
      alert('Tạo task thành công!');
    } catch (error) {
      alert('Error creating task: ' + error.response?.data?.error || error.message);
    } finally {
      setLoading(false);
    }
  };

  const uploadImage = async (file) => {
    const formData = new FormData();
    formData.append('image', file);
    
    try {
      const response = await axios.post('/api/upload-image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      setUploadedImagePath(response.data.image_path);
      return response.data.image_path;
    } catch (error) {
      alert('Error uploading image: ' + error.response?.data?.error || error.message);
      return null;
    }
  };

  const handleImageSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      uploadImage(file); // Upload ngay khi chọn
    }
  };

  const deleteTask = async (taskId) => {
    if (window.confirm('Bạn có chắc muốn xóa task này?')) {
      try {
        await axios.delete(`/api/tasks/${taskId}`);
        loadTasks();
      } catch (error) {
        alert('Error deleting task: ' + error.response?.data?.error || error.message);
      }
    }
  };

  const addPrompt = () => {
    setPrompts([...prompts, '']);
  };

  const updatePrompt = (index, value) => {
    const newPrompts = [...prompts];
    newPrompts[index] = value;
    setPrompts(newPrompts);
  };

  const removePrompt = (index) => {
    if (prompts.length > 1) {
      const newPrompts = prompts.filter((_, i) => i !== index);
      setPrompts(newPrompts);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle size={16} className="text-green-400" />;
      case 'processing':
        return <Clock size={16} className="text-blue-400" />;
      case 'failed':
        return <XCircle size={16} className="text-red-400" />;
      default:
        return <AlertCircle size={16} className="text-gray-400" />;
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'completed':
        return 'Hoàn thành';
      case 'processing':
        return 'Đang xử lý';
      case 'failed':
        return 'Thất bại';
      case 'partial':
        return 'Một phần';
      default:
        return 'Chờ xử lý';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return 'bg-green-500/20 text-green-400';
      case 'processing':
        return 'bg-blue-500/20 text-blue-400';
      case 'failed':
        return 'bg-red-500/20 text-red-400';
      case 'partial':
        return 'bg-yellow-500/20 text-yellow-400';
      default:
        return 'bg-gray-500/20 text-gray-400';
    }
  };

  return (
    <div className="app">
      {/* Left Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h1 className="logo">Gemini AI</h1>
        </div>
        
        <nav className="sidebar-nav">
          <button 
            className={`nav-item ${activeTab === 'home' ? 'active' : ''}`}
            onClick={() => setActiveTab('home')}
          >
            <Home size={20} />
            <span>Home</span>
          </button>
          
          <button 
            className={`nav-item ${activeTab === 'explore' ? 'active' : ''}`}
            onClick={() => setActiveTab('explore')}
          >
            <Search size={20} />
            <span>Explore</span>
          </button>
          
          <button 
            className={`nav-item ${activeTab === 'saved' ? 'active' : ''}`}
            onClick={() => setActiveTab('saved')}
          >
            <Bookmark size={20} />
            <span>Saved</span>
          </button>
          
          <button 
            className={`nav-item ${activeTab === 'messages' ? 'active' : ''}`}
            onClick={() => setActiveTab('messages')}
          >
            <MessageCircle size={20} />
            <span>Messages</span>
          </button>
          
          <button 
            className={`nav-item ${activeTab === 'share' ? 'active' : ''}`}
            onClick={() => setActiveTab('share')}
          >
            <Share2 size={20} />
            <span>Share & Earn</span>
          </button>
          
          <button 
            className={`nav-item ${activeTab === 'settings' ? 'active' : ''}`}
            onClick={() => setActiveTab('settings')}
          >
            <Settings size={20} />
            <span>Settings</span>
          </button>
          
          <button 
            className={`nav-item ${activeTab === 'help' ? 'active' : ''}`}
            onClick={() => setActiveTab('help')}
          >
            <HelpCircle size={20} />
            <span>Help & Support</span>
          </button>
        </nav>
        
        <div className="music-player">
          <div className="music-info">
            <div className="music-wave">
              <div className="wave-bar"></div>
              <div className="wave-bar"></div>
              <div className="wave-bar"></div>
              <div className="wave-bar"></div>
            </div>
            <div className="music-text">
              <p className="song-title">Gemini AI Studio</p>
              <p className="song-artist">Google AI</p>
            </div>
          </div>
          <div className="music-controls">
            <button className="control-btn">
              <Play size={16} />
            </button>
            <button className="control-btn">
              <SkipForward size={16} />
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Header Banner */}
        <div className="banner">
          <div className="banner-content">
            <h2>Create Images with Gemini AI</h2>
            <p>Generate beautiful images from text prompts using multiple API keys</p>
          </div>
        </div>

        {/* Featured Sections */}
        <div className="featured-sections">
          <div className="section-card">
            <div className="section-icon">
              <Settings size={24} />
            </div>
            <h3>API Keys</h3>
            <p>Manage your Gemini API keys</p>
          </div>
          
          <div className="section-card">
            <div className="section-icon">
              <Plus size={24} />
            </div>
            <h3>Batch Create</h3>
            <p>Generate multiple images at once</p>
          </div>
        </div>

        {/* Content Area */}
        <div className="content-area">
          {activeTab === 'home' && (
            <div className="home-content">
              {/* API Keys Management */}
              <div className="content-section">
                <h3>API Keys Management</h3>
                <div className="api-keys-container">
                  <div className="add-key-form">
                    <input
                      type="text"
                      placeholder="Nhập API key mới..."
                      value={newApiKey}
                      onChange={(e) => setNewApiKey(e.target.value)}
                      className="key-input"
                    />
                    <button onClick={addApiKey} className="add-btn">
                      <Plus size={16} />
                      Add
                    </button>
                  </div>
                  
                  <div className="keys-list">
                    {apiKeys.map((key, index) => (
                      <div key={index} className="key-item">
                        <span className={`key-status ${key.status}`}></span>
                        <span className="key-text">****{key.key}</span>
                        <button 
                          onClick={() => removeApiKey(key.key)}
                          className="remove-btn"
                        >
                          <Trash2 size={14} />
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

                             {/* Image Upload */}
               <div className="content-section">
                 <h3>Upload Character Image (Optional)</h3>
                 <div className="image-upload-container">
                   <div className="upload-area">
                     <input
                       type="file"
                       accept="image/*"
                       onChange={handleImageSelect}
                       id="image-upload"
                       className="file-input"
                     />
                     <label htmlFor="image-upload" className="upload-label">
                       <Upload size={24} />
                       <span>Chọn ảnh nhân vật</span>
                       <span className="upload-hint">Chỉ hỗ trợ: JPG, PNG, WebP</span>
                     </label>
                   </div>
                   
                   {selectedImage && (
                     <div className="selected-image">
                       <div className="image-preview">
                         <img src={URL.createObjectURL(selectedImage)} alt="Selected character" />
                       </div>
                       <div className="image-info">
                         <p>📁 {selectedImage.name}</p>
                         <p>📏 {(selectedImage.size / 1024 / 1024).toFixed(2)} MB</p>
                         <p className="upload-status">
                           {uploadedImagePath ? '✅ Đã upload' : '⏳ Đang upload...'}
                         </p>
                       </div>
                     </div>
                   )}
                 </div>
               </div>

               {/* Batch Creation */}
               <div className="content-section">
                 <h3>Create Batch</h3>
                 <div className="batch-form">
                   <div className="form-group">
                     <label>Batch Name:</label>
                     <input
                       type="text"
                       value={batchName}
                       onChange={(e) => setBatchName(e.target.value)}
                       className="form-input"
                     />
                   </div>
                  
                  <div className="prompts-container">
                    <label>Prompts:</label>
                    {prompts.map((prompt, index) => (
                      <div key={index} className="prompt-input-group">
                        <input
                          type="text"
                          placeholder="Nhập prompt để tạo ảnh..."
                          value={prompt}
                          onChange={(e) => updatePrompt(index, e.target.value)}
                          className="form-input"
                        />
                        {prompts.length > 1 && (
                          <button 
                            onClick={() => removePrompt(index)}
                            className="remove-prompt-btn"
                          >
                            <XCircle size={16} />
                          </button>
                        )}
                      </div>
                    ))}
                    <button onClick={addPrompt} className="add-prompt-btn">
                      <Plus size={16} />
                      Add Prompt
                    </button>
                  </div>
                  
                  <button 
                    onClick={createBatchTask}
                    disabled={loading}
                    className="create-btn"
                  >
                    {loading ? 'Creating...' : 'Create Batch'}
                  </button>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'explore' && (
            <div className="tasks-content">
              <h3>Tasks History</h3>
              <div className="tasks-grid">
                {tasks.map((task) => (
                  <div key={task.id} className="task-card">
                    <div className="task-header">
                      <h4>{task.name}</h4>
                      <div className={`task-status ${getStatusColor(task.status)}`}>
                        {getStatusIcon(task.status)}
                        <span>{getStatusText(task.status)}</span>
                      </div>
                    </div>
                    
                    <div className="task-progress">
                      <div className="progress-bar">
                        <div 
                          className="progress-fill"
                          style={{width: `${(task.completed_count / task.total_count) * 100}%`}}
                        ></div>
                      </div>
                      <span className="progress-text">
                        {task.completed_count}/{task.total_count}
                      </span>
                    </div>
                    
                    <div className="task-details">
                      <p>Created: {new Date(task.created_at).toLocaleString()}</p>
                      <p>Failed: {task.failed_count}</p>
                    </div>
                    
                    <div className="task-actions">
                      <button 
                        onClick={() => deleteTask(task.id)}
                        className="delete-task-btn"
                      >
                        <Trash2 size={16} />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Right Sidebar */}
      <div className="right-sidebar">
        <div className="user-profile">
          <div className="avatar">
            <div className="avatar-content">AI</div>
          </div>
          <h3>Hello, User</h3>
        </div>
        
        <div className="status-section">
          <h4>System Status</h4>
          <div className="status-items">
            <div className="status-item">
              <span>Total API Keys:</span>
              <span className="status-value">{status.total_api_keys || 0}</span>
            </div>
            <div className="status-item">
              <span>Available Keys:</span>
              <span className="status-value">{status.available_api_keys || 0}</span>
            </div>
            <div className="status-item">
              <span>Total Tasks:</span>
              <span className="status-value">{status.total_tasks || 0}</span>
            </div>
            <div className="status-item">
              <span>Processing:</span>
              <span className="status-value">{status.processing_tasks || 0}</span>
            </div>
            <div className="status-item">
              <span>Completed:</span>
              <span className="status-value">{status.completed_tasks || 0}</span>
            </div>
          </div>
        </div>
        
        <div className="social-section">
          <h4>Follow Us</h4>
          <div className="social-buttons">
            <button className="social-btn twitter">
              <span>Twitter</span>
            </button>
            <button className="social-btn github">
              <span>GitHub</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
